mapper.insert:
INSERT INTO product_table (
    category_id, name, sub_name, manufacturer, specification, approval_number,
    is_prescription, price, stock, cover_url, description, status
) VALUES (
    #{categoryId}, #{name}, #{subName}, #{manufacturer}, #{specification}, #{approvalNumber},
    #{isPrescription}, #{price}, #{stock}, #{coverUrl}, #{description}, #{status}
)

mapper.update:
UPDATE product_table
SET stock = stock - #{quantity}
WHERE id = #{id}
  AND status = 1
  AND stock >= #{quantity}

UPDATE product_table
SET stock = stock + #{quantity}
WHERE id = #{id}

mapper.select:
SELECT 
FROM product_table

    status = 1

        AND category_id = #{categoryId}


        AND (name LIKE CONCAT('%', #{keyword}, '%') OR sub_name LIKE CONCAT('%', #{keyword}, '%'))


        AND price >= #{minPrice}


        AND price <= #{maxPrice}


        AND is_prescription = #{isPrescription}


ORDER BY id DESC
